            <div class="invoice-date">
              22 December 2017
            </div>
          </div>
          <div class="invoice-body">
            <div class="invoice-desc">
              <div class="desc-label">
                Invoice #
              </div>
              <div class="desc-value">
                HSFB 342823
              </div>
            </div>
            <div class="invoice-table">
              <table class="table">
                <thead>
                  <tr>
                    <th>
